const { findDocumentById } = require("../models/event-cloudant");
const QRCode = require("qrcode");
const { createCanvas, loadImage } = require("canvas");

const createQrFun = async (dataForQRcode, center_image, width, cwidth) => {
  const canvas = createCanvas(width, width);
  QRCode.toCanvas(canvas, dataForQRcode, {
    errorCorrectionLevel: "H",
    margin: 1,
    color: {
      dark: "#000000",
      light: "#ffffff",
    },
  });

  const ctx = canvas.getContext("2d");
  const img = await loadImage(center_image);
  const center = (width - cwidth) / 2;
  ctx.drawImage(img, center * 1.4, center * 1.5, cwidth, cwidth);
  return canvas.toDataURL("image/png");
};

const renderQr = async (link) => {
  const qrCode = await createQrFun(
    link,
    "data:image/jpeg;base64,",
    150,
    50
  );

  return qrCode;
};

