const generateEventQr = async (req, res) => {
  const { eventId } = req.params;

  if (!eventId) {
    return res.status(400).json({ msg: "Invalid Request!" });
  }

  // find event
  let event;
  try {
    event = await findDocumentById(eventId);
  } catch (err) {
    console.log(err);
    return res.status(500).json({ msg: "Event not found!" });
  }

  const link = `http://qyagrifresh.com/event/${eventId}`;

  const qr = await renderQr(link);
  res.status(200).json({ qr, msg: "Qr created!" });
};

module.exports = {
  generateEventQr,
};
